<?phprequire('vendor/autoload.php');header("Content-Type: json");use Cloutier\PhpIpfsApi\IPFS;$ipfs = new IPFS("localhost", "8080", "5001");if (isset($_GET['hash'])){	$hash = $_GET['hash'];	// check the disk for the hash	$file = '../data/json/'.$hash.'.json';	if (file_exists($file))	{		echo file_get_contents($file);	}	else	{		// check IPFS for the hash if not on disk		$json = $ipfs->cat($hash);		if (!isJson($json))// == "Path Resolve error: not a key")		{			$json = array('hash' => $hash, 'children' => array());			echo json_encode($json);		}		else		{			echo $json;		}	}}else{	echo json_encode(array('error' => 'NO_DATA'));}function isJson($string){	json_decode($string);	return (json_last_error() == JSON_ERROR_NONE);}?>